target.script:
var props = LWJGL.getProperties();

var jvmargs = ["-Dbinding.DISABLE_CHECKS=" + props.get("binding.DISABLE_CHECKS")];

var iter = props.entrySet().iterator();
var c = 0;
while ( iter.hasNext() ) {
    var prop = iter.next();
    var name = prop.getKey();
    if ( name.startsWith("binding.") && name.indexOf('_') === -1 && prop.getValue() == "true" )
        jvmargs.push("-D" + name + "=true");
}

LWJGL.setProperty("bindings", jvmargs.join(" "));
